CustomField.formula:
IF(  
OR(ISPICKVAL( Status__c ,'Canceled'),
AND(Updated_Locate__c, Expiration_Date__c - TODAY() < 0 )
),IMAGE("/resource/Locate_Grey", "Locate Grey", 15, 15),

IF(AND( RecordType.DeveloperName = 'Locate',
Expiration_Date__c  - TODAY() > 3),IMAGE("/resource/Locate_Green", "Locate Green", 15, 15),

IF(AND(RecordType.DeveloperName = 'Locate',Expiration_Date__c - TODAY() <= 3,Expiration_Date__c - TODAY() > 0),IMAGE("/resource/Locate_Yellow", "Locate Yellow", 15, 15),

IF(AND( RecordType.DeveloperName = 'Permit',
Expiration_Date__c  - TODAY() > Authority_Having_Jurisdiction__r.Permit_Turnaround_Time__c ),IMAGE("/resource/Locate_Green", "Locate Green", 15, 15),

IF(AND( RecordType.DeveloperName = 'Permit', 
Expiration_Date__c - TODAY() <= Authority_Having_Jurisdiction__r.Permit_Turnaround_Time__c , Expiration_Date__c - TODAY() > 0),IMAGE("/resource/Locate_Yellow", "Locate Yellow", 15, 15),

IF(Expiration_Date__c - TODAY() <= 0,IMAGE("/resource/Locate_Red", "Locate Red", 15,15),
" "
)
)
)
)
)
)
